    private void aceptarBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_aceptarBtnActionPerformed
        // TODO add your handling code here:
        boolean esTrifasico = false;
        double KW = Double.parseDouble(consumoKW.getText());
        if (monoRdBtn.isSelected()){
            esTrifasico = false;
        }else{
            esTrifasico = true;
        }
        Luz luz = new Luz(3.3, esTrifasico, KW);
        
        double consumoMetrosCubicos = Double.parseDouble(consumoMetrosCub.getText());
        Agua agua = new Agua(consumoMetrosCubicos, 2.5);
        
        luz.calcularMontoFinal();
        agua.calcularMontoFinal();
        
        totalLuzTxt.setText("S/ "+String.valueOf(luz.montoFinal));
        totalAguaTxt.setText("S/ "+String.valueOf(agua.montoFinal));
        
    }//GEN-LAST:event_aceptarBtnActionPerformed

    private void borrarBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_borrarBtnActionPerformed
        // TODO add your handling code here:
        consumoKW.setText(null);
        consumoMetrosCub.setText(null);
        totalLuzTxt.setText(null);
        totalAguaTxt.setText(null);
        
    }//GEN-LAST:event_borrarBtnActionPerformed

